import React from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import highchartsMap from "highcharts/modules/map";
import mapDataIE from "@highcharts/map-collection/countries/us/us-all.geo.json";
highchartsMap(Highcharts); // Initialize the map module

const western_states = [
  ['us-ca', "#20e012"],
  ['us-or', "#20e012"],
  ['us-id', "#20e012"],
  ['us-mt', "#20e012"],
  ['us-az', "#20e012"],
  ['us-nv', "#20e012"],
  ['us-wa', "#20e012"],
  ['us-ut', "#20e012"]
];

const central_states = [
  ['us-wy', "#BADA55"],
  ['us-nd', "#BADA55"],
  ['us-sd', "#BADA55"],
  ['us-co', "#BADA55"],
  ['us-ks', "#BADA55"],
  ['us-ky', "#BADA55"],
  ['us-in', "#BADA55"],
  ['us-wi', "#BADA55"],
  ['us-ne', "#BADA55"],
  ['us-ia', "#BADA55"],
  ['us-mi', "#BADA55"],
  ['us-mo', "#BADA55"],
  ['us-il', "#BADA55"],
  ['us-mn', "#BADA55"],
  ['undefined', "#BADA55"]
];

const eastern_states = [
  ['us-oh', "#003fed"],
  ['us-ny', "#003fed"],
  ['us-nc', "#003fed"],
  ['us-vt', "#003fed"],
  ['us-wv', "#003fed"],
  ['us-md', "#003fed"],
  ['us-de', "#003fed"],
  ['us-ri', "#003fed"],
  ['us-ma', "#003fed"],
  ['us-me', "#003fed"],
  ['us-nh', "#003fed"],
  ['us-sc', "#003fed"],
  ['us-va', "#003fed"],
  ['us-nj', "#003fed"],
  ['us-dc', "#003fed"],
  ['us-ct', "#003fed"],
  ['us-pa', "#003fed"]
];

const southern_states = [
  ['us-tx', "#e08a12"],
  ['us-nm', "#e08a12"],
  ['us-tn', "#e08a12"],
  ['us-ga', "#e08a12"],
  ['us-fl', "#e08a12"],
  ['us-ar', "#e08a12"],
  ['us-ok', "#e08a12"],
  ['us-la', "#e08a12"],
  ['us-al', "#e08a12"],
  ['us-hi', "#e08a12"],
  ['us-ak', "#e08a12"],
  ['us-ms', "#e08a12"]
];

class Chart extends React.PureComponent {

  constructor(props) {
      super(props);

      this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
  
      this.state = {
          mapOptions:  {
              chart: {
                height: this.props.height,

                map: "countries/ie/ie-all",
                events: {
                    drilldown: function(e) {
                      console.log('from drillDown');
                    },
                    drillup: function() {
                      console.log('from drillUp');
                    }
                  },
                panning: true,
                panKey: 'shift'
              },
              tooltip: { 
                enabled: true,
                formatter: function () {
                  return '<b>' + this.point.name + '</b>';
                },
              },
              legend: {
                layout: "vertical",
                align: "right",
                verticalAlign: "middle",
                enabled: false
              },
            
              title: {
                text: null
              },
            
              mapNavigation: {
                enabled: false,
                buttonOptions: {
                  verticalAlign: "bottom"
                }
              },
            
              plotOptions: {
                map: {
                  states: {

                    color: "#126c31",

                    hover: {
                      color: "#000000",
                      enabled: true,
                      brightness: 0.5
                    },
                    
                  }
                },
                series:{
                    point:{
                        events:{
                            click: this.setHoverData.bind(this)
                        }
                    }
                }
              },
            
              series: [
                // {
                //   data: data,
                //   mapData: mapDataIE,
                //   name: "USA",
                //   dataLabels: {
                //       enabled: true,
                //       format: "{point.properties.postal-code}"
                //   }
                // },

                {
                  data: western_states.concat(central_states).concat(eastern_states).concat(southern_states),
                  keys: ['hc-key', 'color'],
                  mapData: mapDataIE,
                  name: " ",
                  dataLabels: {
                      enabled: true,
                      color: '#FFFFFF',
                      format: "{point.properties.postal-code}"
                  },
                  showInLegend: false
                },

              ],
              drilldown: {
                activeDataLabelStyle: {
                  color: "#FFFFFF",
                  textDecoration: "none",
                  textOutline: "1px #000000"
                },
                drillUpButton: {
                  relativeTo: "spacingBox",
                  position: {
                    x: 0,
                    y: 60
                  }
                }
              }
            }
      };
    }

    setHoverData = (e) => {
      // console.log(e.point["hc-key"])
      this.props.callbackFromApp(e.point["hc-key"])
    }

    componentDidMount() {
      this.updateWindowDimensions();
      window.addEventListener('resize', this.updateWindowDimensions);
    }
    
    componentWillUnmount() {
      window.removeEventListener('resize', this.updateWindowDimensions);
    }
  
    
    updateWindowDimensions() {
      
      // let div_width2 = this.divRef.current.clientWidth;
      // let div_width = div_width2 - 2*85;
      // let height = this.props.height - 2*85;
  
      this.setState({
        mapOptions:  {
          chart: {
            height: this.props.height,

            map: "countries/ie/ie-all",
            events: {
                drilldown: function(e) {
                  console.log('from drillDown');
                },
                drillup: function() {
                  console.log('from drillUp');
                }
              },
            panning: true,
            panKey: 'shift'
          },
          tooltip: { 
            enabled: true,
            formatter: function () {
              return '<b>' + this.point.name + '</b>';
            },
          },
          legend: {
            layout: "vertical",
            align: "right",
            verticalAlign: "middle",
            enabled: false
          },
        
          title: {
            text: null
          },
        
          mapNavigation: {
            enabled: false,
            buttonOptions: {
              verticalAlign: "bottom"
            }
          },
        
          plotOptions: {
            map: {
              states: {

                color: "#126c31",

                hover: {
                  color: "#000000",
                  enabled: true,
                  brightness: 0.5
                },
                
              }
            },
            series:{
                point:{
                    events:{
                        click: this.setHoverData.bind(this)
                    }
                }
            }
          },
        
          series: [
            {
              data: western_states.concat(central_states).concat(eastern_states).concat(southern_states),
              keys: ['hc-key', 'color'],
              mapData: mapDataIE,
              name: " ",
              dataLabels: {
                  enabled: true,
                  color: '#FFFFFF',
                  format: "{point.properties.postal-code}"
              },
              showInLegend: false
            },
          ],
        
          drilldown: {
            activeDataLabelStyle: {
              color: "#FFFFFF",
              textDecoration: "none",
              textOutline: "1px #000000"
            },
            drillUpButton: {
              relativeTo: "spacingBox",
              position: {
                x: 0,
                y: 60
              }
            }
          }
        }         
      });
    }
  
    render() {
      return (
          <div className="map_content">
              <HighchartsReact
                  constructorType={"mapChart"}
                  highcharts={Highcharts}
                  options={this.state.mapOptions} 
              />
          </div>
      );
      }
  }

  export default Chart
