import React, { Component } from 'react';
import { Button,Form } from 'react-bootstrap';
import logo from '../assets/logo.png';


class LoginComponent extends Component {	

  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.handleChangeLogin = this.handleChangeLogin.bind(this);
    this.handleChangePassword = this.handleChangePassword.bind(this);
    this.state = {
        authenticated: true,
        username: "",
        password: "",
        displayError: false,
        error_message: "Incorrect Username and Password"
    };
  }

  handleClick(event){
    event.preventDefault();
    if (this.state.username === "user" && this.state.password === "user") {
      this.props.callbackFromLogin(this.state);
    } else {
      this.errorCallback()
    }
  }

  errorCallback = () => {

    this.setState({
      displayError: true
    });

    window.setTimeout(() => {
      this.setState({
        displayError: false
      });
    }, 2000);

  }

  handleChangeLogin(event) {
    this.setState({
        username: event.target.value
    });
  }

  handleChangePassword(event) {
    this.setState({
        password: event.target.value
    });
  }


  render() {

    return (
    <div>

      <div className="loginform">
      <div className="logo">
        <img src={logo} className="kloud9" />
      </div>

      <h2>Login page</h2>
      <br/>

      <h5>Username: user</h5>
      <h5>Password: user</h5>
      { (this.state.displayError) ? (<div className="error_message">{this.state.error_message}</div>) : (<div><br/></div>)}
      <br/>
          <Form onSubmit={this.handleClick}>
            <Form.Group controlId="formBasicUsername">
                <Form.Label>Username</Form.Label>
                <Form.Control type="text" placeholder="Enter username" value={this.state.keywords} onChange={this.handleChangeLogin} className="input_username" />
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" value={this.state.keywords} onChange={this.handleChangePassword} className="input_password" />
            </Form.Group>
            <Button variant="primary" type="submit" className="btn-blue">
                Login
            </Button>
          </Form>

      </div>
    </div>

		);

  }


}

export default LoginComponent
